CREATE TABLE  IF NOT EXISTS recomendaciones
(                                                                                                                                                                                                                                                                                    
    `user_id` UUID,                                                                                                                                                                                                                                                                  
    `item_id` UInt64,                                                                                                                                                                                                                                                                
    `score` Float32,                                                                                                                                                                                                                                                                 
    `timestamp` DateTime                                                                                                                                                                                                                                                             
)                                                                                                                                                                                                                                                                                    
ENGINE = MergeTree                                                                                                                                                                                                                                                                   
ORDER BY (user_id, item_id)